import React,{ Component } from "react";
import { Link } from "react-router-dom";
import MyContext from "../Context/context";

export default class AddMovie extends Component {
  constructor() {
    super();

    this.state = {
      filmes: [],
      filme: '',
      categories: '',
    }
  }

  handleClick = ({ target }) => {
    const { name, value } = target;
    this.setState=({
      [name]: value,
    });
  }

  render() {
    return (
      <MyContext.Consumer>
        {
          <div>
            <form>
          <label>
            Nome:
            <input type="text" name="filme" onChange={this.handleClick} />
          </label>
          <label>
            Categoria:
            <input type="text" name="Categoria" onChange={this.handleClick} />
          </label>
          <button type="button">Adicionar filme</button>
          <Link to="/filmes" >Filmes Adicionados</Link>
        </form>
          </div>
        }
      </MyContext.Consumer>
    );
  }
}
