import React,{ Component } from "react";
import { Link } from "react-router-dom";
import MyContext from "../Context/context";

export default class AddMovie extends Component {
  constructor() {
    super();

    this.state = {
      filmes: [],
      filme: '',
      categories: '',
    }
  }

  handleChange = ({ target: {name, value} }) => {
    this.setState({ [name]: value })
  }

  handleAddMovies = () => {
    const { add } = this.context;
    add({...this.state});
  }

  render() {
    console.log(this.context);
    return (
      <>
        <form>
          <label>
            Nome:
            <input type="text" name="filme" onChange={this.handleChange} />
          </label>
          <label>
            Categoria:
            <input type="text" name="Categoria" onChange={this.handleChange} />
          </label>
          <button onClick={ handleAddMovies } type="button">Adicionar filme</button>
          <Link to="/filmes" >Filmes Adicionados</Link>
        </form>
      </>
    );
  }
}

AddMovie.contextType = MyContext;
