import React from 'react';

export default class Form extends React.Component {
constructor() {
  super()
  this.state = {
    endereco: "",
  }
}

handleAddress = ({ target }) => {
  const str = target.value;
  this.setState({endereco: str.replace(/[^\w\p]/g, "")})
}

render() {
  return(
    <form>
      <fieldset>
        <label htmlFor="name">
          Nome:
          <input name="name" type="name" id="name" required maxLength="30" />
        </label>

        <label html="email">
          Email:
          <input name="email" type="email" id="email" maxLength="50" required/>
        </label>

        <label htmlFor="cpf">
          CPF:
          <input type="text" required id="cpf" maxLength="11" placeholder="12345678910" />
        </label>

        <label>
          Endere√ßo:
          <input type="textArea" maxLength="200" value={this.state.endereco} required onChange={this.handleAddress}/>
        </label>
      </fieldset>
    </form>
  );
};
}
