import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { actionChangeName } from '../redux/actions';
import { connect } from 'react-redux';

class Nome extends Component {
  constructor() {
    super();

    this.state = {
      nome: '',
    }
  }

  handleInput = ({ target: { value } }) => {
    this.setState({ nome: value })
  }



  render() {
    const { ChangeName } = this.props;

    return(
      <div>
        <h1>Digite um nome:</h1>
        <input type="text" onChange={ this.ChangeName }/>
        <Link to='preferencia'>Próximo</Link>
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    ChangeName: (name) => dispatch(actionChangeName(name))
  }
  // Eu vou criar um obj, que tem uma chave chamada 'ChanceName'.
  // ChangeName vai ser uma função, que recebe um nome e dispara uma ação com o nome que ele recebeu.
};

export default connect(null, mapDispatchToProps)(Nome);
// O connect joga esses dois parametro para dentro do componente 'nome'
// Primeiro paramentro é de leitura e o segundo e de escrita.
