import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { actionChangeName } from '../redux/actions';
import { connect } from 'react-redux';

class Nome extends Component {
  render() {
    return(
      <div>
        <h1>Digite um nome:</h1>
        <input type="text" onChange={ actionChangeName }/>
        <Link to='preferencia'>Próximo</Link>
      </div>
    );
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Nome);
// O connect joga esses dois parametro para dentro do componente 'nome'
// Primeiro paramentroé de leitura e o segundo e de escrita.
